import React from 'react';
import logo from './logo.svg';
import './App.css';

class Welcome extends React.Component {
  constructor (props){
    super(props);
    this.state={
      items: [],
      currentItem: {
        text:"" ,
        key:""
      }
    }
    this.handleInput = this.handleInput.bind(this);
  }
  handleInput(e){
    this.setState ({
      currentItem: {
        text: e.target.value,
        key: Date.now()
      }
    })
  }
  addItem(e) {
    e.preventDefault();
    const newItem = this.state.currentItem;
  }
  render() {
    return (
      <div className="AppContainer">
        <header>
          <form id="to-do-form">
            <input type="text" placeholder="Get it done..." value={this.state.currentItem.text}
            onChange={this.handleInput} />
            <button type="submit"> Add </button>
          </form>
        </header>
      </div>

    );
  }
}

export default Welcome;
